document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".toggle-btn").forEach(t=>{t.addEventListener("click",()=>{var e=t.previousElementSibling.querySelector(".job-description");e.classList.contains("hidden")?(e.classList.remove("hidden"),t.textContent="Show Less"):(e.classList.add("hidden"),t.textContent="Show More")}),t.addEventListener("mouseover",()=>{t.style.backgroundColor="#232323",t.style.color="#ffcb2e"}),t.addEventListener("mouseout",()=>{t.style.backgroundColor="#ffcb2e",t.style.color="#232323"}),t.addEventListener("focus",()=>{t.style.outline="none"}),t.addEventListener("blur",()=>{t.style.outline="none"})})});